import { useState } from 'react'
import { FlexProvider, useFlexContext, useFlexRequest } from './flex-react'

function App() {
  return (
    <FlexProvider url='http://localhost:8080'>
      <div style={{ padding: '1rem'}}>
        <h1 style={{ marginTop: 0 }}>Flex React Example</h1>
        <Monitor />
        <hr />
        <Component1 />
        <hr />
        <Component2 />
        <hr />
        <Component3 />
        <hr />
        <Component4 />
      </div>
    </FlexProvider>
  );
}

const Monitor = () => {
  const { queue, state } = useFlexContext()
  console.log(queue, state)
  return null
}

const Component1 = () => {
  const { data, error, loading } = useFlexRequest('hello', null, ['hello'])
  return (
    <div>
      <h3>{`["hello", null, ["hello"]]`}</h3>
      <p>{loading ? 'Loading...' : error ? 'Error: ' + error.message : JSON.stringify(data)}</p>
    </div>
  )
}

const Component2 = () => {
  const [name, setName] = useState('Steve')
  const { data, error, loading } = useFlexRequest('greet', { name })
  return (
    <div>
      <h3>{`["greet", {"name":"`}<input type='text' value={name} onChange={(e) => setName(e.target.value)} />{`"}]`}</h3>
      <p>{loading ? 'Loading...' : error ? 'Error: ' + error.message : JSON.stringify(data)}</p>
    </div>
  )
}

const Component3 = () => {
  const { data, error, loading } = useFlexRequest('fail')
  return (
    <div>
      <h3>{`["fail"]`}</h3>
      <p>{loading ? 'Loading...' : error ? 'Error: ' + error.message : JSON.stringify(data)}</p>
    </div>
  )
}

const Component4 = () => {
  const { data, error, loading } = useFlexRequest('missing')
  return (
    <div>
      <h3>{`["missing"]`}</h3>
      <p>{loading ? 'Loading...' : error ? 'Error: ' + error.message : JSON.stringify(data)}</p>
    </div>
  )
}

export default App;
