import { useState, useEffect, useRef, createContext, useContext, useCallback } from 'react'

const FlexContext = createContext()

export const FlexProvider = ({ children, options }) => {

  const [queue, setQueue] = useState([])
  const [state, setState] = useState({})

  const timeout = useRef()

  const enqueue = useCallback((id, name, variables, options) => {
    if (timeout.current) clearTimeout(timeout.current)
    setState((state) => {
      return {
        ...state,
        [id]: {
          loading: false,
          error: null,
          data: null
        }
      }
    })
    setQueue((queue) => [...queue, [id, name, variables, options]])
  }, [])

  useEffect(() => {
    if (queue.length > 0) {
      const myQueue = queue.map((q) => [...q])
      setQueue([])
      timeout.current = setTimeout(() => {
        setState((state) => {
          const newState = {
            ...state
          }
          for (let i = 0; i < myQueue.length; i++) {
            const item = myQueue[i]
            newState[item[0]] = {
              loading: true,
              error: null,
              data: null
            }
          }
          return newState
        })
        fetch({
          ...options,
          body: myQueue
        })
        .then(async (result) => {
          const results = await result.json()
          setState((state) => {
            const newState = {
              ...state
            }
            for (let i = 0; i < results.length; i++) {
              const item = result[i]
              newState[item[0]] = {
                loading: false,
                error: result[3],
                data: result[2]
              }
            }
            return newState
          })
        })
        .catch((error) => {
          setState((state) => {
            const newState = {
              ...state
            }
            for (let i = 0; i < myQueue.length; i++) {
              const item = myQueue[i]
              newState[item[0]] = {
                loading: false,
                error: error,
                data: null
              }
            }
            return newState
          })
        })
      }, 1)
    }
  }, [queue, options])

  return (
    <FlexContext.Provider value={{ queue, state, enqueue }}>
      {children}
    </FlexContext.Provider>
  )

}

export const useFlexContext = () => {

  const context = useContext(FlexContext)

  console.warn('useFlexContext() is a utility function for debugging')

  return context

}

export const useFlexRequest = (name, variables, options) => {

  const { state, enqueue } = useContext(FlexContext)
  const [requestId, setRequestId] = useState(null)
  const queryState = requestId && state[requestId]

  useEffect(() => {
    console.log('this')
    const id = Math.round(Math.random() * 1000000).toString()
    enqueue([id, name, variables, options])
    setRequestId(id)
  }, [name, variables, options, enqueue])

  return queryState || {}

}

export const useFlexCommand = (name, options) => {

  const { state, enqueue } = useContext(FlexContext)
  const [requestId, setRequestId] = useState(null)
  const queryState = requestId && state[requestId]

  const request = useCallback((variables) => {
    const id = Math.round(Math.random() * 1000000).toString()
    enqueue([id, name, variables, options])
    setRequestId(id)
  }, [name, options, enqueue, setRequestId])

  return [request, queryState || {}]
}
